import React from "react";

import DeleteIcon from "@mui/icons-material/Delete";
import ArrowDownwardIcon from "@mui/icons-material/ArrowDownward";
import ArrowUpwardIcon from "@mui/icons-material/ArrowUpward";

function List(props) {
  return (
    <ul className="list-group mt-2 p-0">
      {props.items.map((item) => {
        return (
          <li className="list-group-item d-flex flex-row align-items-center" key={item.id}>
            <span className="taskName flex-grow-1">{item.value}</span>      
            <button onClick={() => props.itemUp(item.id)}>
                <ArrowUpwardIcon />
              </button>
            <button onClick={() => props.itemDown(item.id)}>
                <ArrowDownwardIcon />
              </button>
              <button onClick={() => props.removeItem(item.id)}>
                <DeleteIcon />
              </button>
          </li>
        );
      })}
    </ul>
  );
}

export default List;
