  return (
    <div className="App">
      {/* 1. Header */}
      <h1>Todo List app</h1>

      {/* 2. Input (input and button) */}

      <input
        type="text"
        placeholder="Add an item"
        onChange={(e) => {
          setNewItem(e.target.value);
        }}
      />

      <button onClick={() => addItem()}>Add</button>

      <List items={items} />
    </div>
  );
}

export default App;
