import React from "react";

function Input(props) {

  const newItem = "";

  return (
    <>
      <input
        type="text"
        placeholder="Add an item"
        onChange={(e) => {
          newItem = e.target.value;
        }}
      />
      <button onClick={() => props.addItem(newItem)}>Add</button>
    </>
  );
}

export default Input;
