import React from "react";

function Input(props) {
  function addItem() {
    if (!props.input) {
      return;
    }
    const item = {
      id: Math.floor(Math.random() * 10000),
      value: input,
    };
    setItems((items) => [...items, item]);
    setInput("");
  }

  return (
    <>
      <input
        type="text"
        placeholder="Add an item"
        onChange={(e) => {
          setInput(e.target.value);
        }}
      />
      <button onClick={() => addItem()}>Add</button>
    </>
  );
}

export default Input;
