import React, { useState } from "react";
import "./App.css";

import Input from "./Components/Input";
import List from "./Components/List";

function App() {
  const [items, setItems] = useState([]);

  function addItem(input) {
    if (!input) {
      return;
    }
    const newItems = [input, ...items];
    setItems(newItems);
    //setItems((items) => [input, ...items]);
  }

  return (
    <div className="App">
      <h1>Todo List app</h1>
      <Input addItem={addItem} />
      <List items={items} />
    </div>
  );
}

/*itemUp={itemUp} itemDown={itemDown} deleteItem={deleteItem}*/

