import React, { useState } from "react";
import { FaBeer } from "react-icons/fa";
import "./App.css";

function App() {
  // State hook
  const [newItem, setNewItem] = useState("");
  const [items, setItems] = useState([]);

  function addItem() {
    if (!newItem) {
      return;
    }

    // console.log(newItem);
    const item = { id: Math.floor(Math.random() * 1000), value: newItem };
    setItems((items) => [...items, item]);
    setNewItem("");
  }

  function deleteItem(id) {
    const newArray = items.filter((item) => item.id !== id);
    setItems(newArray);
  }

  function itemUp(id) {
    const index = items.findIndex((item) => item.id === id);
    if (index === 0) {
      let o = items[0];
      items[0] = items[1];
      items[items.length-1] = o;
    } else {
      let o = items[index - 1];
      items[index - 1] = items[index];
      items[index] = o;
    }

    setItems(newArray);
  }

  return (
    <div className="App">
      {/* 1. Header */}
      <h1>Todo List app</h1>

      {/* 2. Input (input and button) */}

      <input
        type="text"
        placeholder="Add an item"
        onChange={(e) => {
          setNewItem(e.target.value);
        }}
      />

      <button onClick={() => addItem()}>Add</button>

      {/* 3. List of items */}
      <ul>
        {items.map((item) => {
          return (
            <li key={item.id}>
              {item.value}
              <button onClick={() => deleteItem(item.id)}>
                <FaBeer />
              </button>
              <button onClick={() => itemUp(item.id)}>
                <FaBeer />
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default App;
