    <div className="App">
      {/* 1. Header */}
      <h1>Todo List app</h1>

      {/* 2. Input (input and button) */}

      <input 
        type="text"
        placeholder="Add an item"
        value={newItem}
        onChange={changeHandler}
      />

      <button>Add</button>

      {/* 3. List of items */}
      <ul>
        <li>Take out the trash</li>
        <li>Water the plants</li>
        <li>Go to the gym</li>
      </ul>
    </div>
  );
}

export default App;
